// src/components/BookForm.js
import React, { useState, useEffect } from 'react';
import { createBook, updateBook } from '../api';

function BookForm({ bookToEdit, onFormSubmit }) {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [ISBN, setISBN] = useState('');

  useEffect(() => {
    if (bookToEdit) {
      setTitle(bookToEdit.title);
      setAuthor(bookToEdit.author);
      setISBN(bookToEdit.ISBN || '');
    }
  }, [bookToEdit]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const bookData = { title, author, ISBN };

    try {
      if (bookToEdit) {
        await updateBook(bookToEdit.id, bookData);
      } else {
        await createBook(bookData);
      }
      onFormSubmit();
      setTitle('');
      setAuthor('');
      setISBN('');
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{bookToEdit ? 'Edit Book' : 'Add New Book'}</h2>
      <div>
        <label>Title:</label>
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
      </div>
      <div>
        <label>Author:</label>
        <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} required />
      </div>
      <div>
        <label>ISBN:</label>
        <input type="text" value={ISBN} onChange={(e) => setISBN(e.target.value)} />
      </div>
      <button type="submit">{bookToEdit ? 'Update Book' : 'Add Book'}</button>
    </form>
  );
}

export default BookForm;
